    it "should save contacts in one upload" do
        BulkContact.upload(data: @hsh, user_id: @current_user.id)
        bcs = BulkContact.find_by(user_id: @current_user.id)
        bcs.parsed_data.should == @hsh
    end

    it "should accept ary of contacts and make array of contact hashes" do
        BulkContact.upload(data: @hsh, user_id: @current_user.id)
        bcs = BulkContact.find_by(user_id: @current_user.id)
        ac = bcs.normalized_data
        ac.count.should == 214
        ac.first["network"].should    == "facebook"
        ac.first["network_id"].should == "12925285"
        ac.first["name"].should       == "Melinda Farmer Edgerton"
    end

    it "should accept hash of contact_id contact info and make array of contact hashes" do
        hsh = { "672342" => { "first_name" => "tommy" ,"last_name" => "hilfigure", "email" => [ "email1@gmail.com", "email2@yahoo.com"], "phone" => [ "3102974545", "6467586473"], "twitter" => [ "2i134o1234123"], "facebook" => [ "23g2381d103dy1"] }, "22" => { "first_name" => "Jenifer" ,"last_name" => "Bowie", "email" => [ "jenny@facebook.com"], "phone" => ["7824657878"]}}
        BulkContact.upload(data: hsh, user_id: @current_user.id)
        bcs = BulkContact.find_by(user_id: @current_user.id)
        ac = bcs.normalized_data
        ac.count.should == 8
        ac.first["network"].should    == "email"
        ac.first["network_id"].should == "email1@gmail.com"
        ac.first["name"].should       == "tommy hilfigure"
    end

    it "should downcase emails" do
        hsh = { "672342" => { "first_name" => "tommy" ,"last_name" => "hilfigure", "email" => [ "Email1@Gmail.com", "emMAIL2@yahoo.cOm"], "phone" => [ "3102974545", "6467586473"], "twitter" => [ "2i134o1234123"], "facebook" => [ "23g2381d103dy1"] }, "22" => { "first_name" => "Jenifer" ,"last_name" => "Bowie", "email" => [ "jenny@facebook.com"], "phone" => ["7824657878"]}}
        BulkContact.upload(data: hsh, user_id: @current_user.id)
        bcs = BulkContact.find_by(user_id: @current_user.id)
        ary = bcs.normalized_data
        formatted = ary.select do |a|
            ["email1@gmail.com",  "emmail2@yahoo.com"].include?(a["network_id"])
        end
        formatted.count.should == 2
        formatted.first["network"].should == 'email'
        formatted.first["name"].should       == "tommy hilfigure"

    end

    it "should extract phone digits" do
        hsh = { "672342" => { "first_name" => "tommy" ,"last_name" => "hilfigure", "email" => [ "Email1@Gmail.com", "emMAIL2@yahoo.cOm"], "phone" => [ "+1310.297.4545", "+1(646)-758 -6473"], "twitter" => [ "2i134o1234123"], "facebook" => [ "23g2381d103dy1"] }, "22" => { "first_name" => "Jenifer" ,"last_name" => "Bowie", "email" => [ "jenny@facebook.com"], "phone" => ["7824657878"]}}
        BulkContact.upload(data: hsh, user_id: @current_user.id)
        bcs = BulkContact.find_by(user_id: @current_user.id)
        ary = bcs.normalized_data
        formatted = ary.select do |a|
            [ "3102974545", "6467586473"].include?(a["network_id"])
        end
        formatted.count.should == 2
        formatted.first["network"].should == 'phone'
        formatted.first["name"].should    == "tommy hilfigure"
    end

    it "should downcase emails and extract phone digits for ary" do
        ary =  [{"birthday"=>"10/05/1987", "network_id"=>"Email1@Gmail.com", "network"=>"email", "name"=>"Taylor Addison", "handle"=>"taylor.addison1" }, {"birthday"=>"10/05/1987", "network_id"=>"+1(646)-758 -6473", "network"=>"phone", "name"=>"Taylor Addison", "handle"=>"taylor.addison1" }]
        BulkContact.upload(data: ary, user_id: @current_user.id)
        bcs = BulkContact.find_by(user_id: @current_user.id)
        ary = bcs.normalized_data
        formatted = ary.select do |a|
            [ "email1@gmail.com", "6467586473"].include?(a["network_id"])
